Get-ChildItem -Include *ObjectModel.dll -Exclude *Tests.dll -Recurse | Where-Object {$_.FullName -notLike '*\obj\*' -and $_.FullName -notMatch "System" -and $_.FullName -notLike '*\Debug\*'} | Compress-Archive -Update -DestinationPath ($PSScriptRoot + "\IronstoneObjectModel")
Write-Host "Zip file created"